$(function(){$("input[name=hex]").click(function(){var hexNum=($(this).val());$("#numEnterArea .hex-num").text(hexNum)});$("#conver").click(function(){var num=$("#numIn").val();var hexChecked=$('input:radio[name="hex"]:checked').val();switch(hexChecked){case"2":$("#outTo2").val(num);$("#outTo8").val(parseInt(num,2).toString(8));$("#outTo10").val(parseInt(num,2));$("#outTo16").val(parseInt(num,2).toString(16));break;case"8":$("#outTo2").val(parseInt(num,8).toString(2));$("#outTo8").val(num);$("#outTo10").val(parseInt(num,8));$("#outTo16").val(parseInt(num,8).toString(16));break;case"10":$("#outTo2").val(parseInt(num).toString(2));$("#outTo8").val(parseInt(num).toString(8));$("#outTo10").val(num);$("#outTo16").val(parseInt(num).toString(16));break;case"16":$("#outTo2").val(parseInt(num,16).toString(2));$("#outTo8").val(parseInt(num,16).toString(8));$("#outTo10").val(parseInt(num,16));$("#outTo16").val(num)}});var clipboard=new Clipboard(".btn");$("#numConver button").hover(function(){$(this).find(".copied-info").text("Copy to clipboard").stop().show()},function(){$(this).find(".copied-info").stop().hide()});$("#numConver button").click(function(){$(this).find(".copied-info").text("Copied").stop().show()})});